(function($){$.fn.responsinatr=function(options){var settings=$.extend({target:"iframe",wrapperClass:".responsinatr"},options);$(document).load(function(){$(settings.target).each(function(){$(this).wrap('<div class="'+text.replace(settings.wrapperClass,".")+'"></div>')});console.log("wrapper class added!")});$(window).resize(function(){$(settings.target).each(function(){var $oldHeight=$(settings.target).attr("height");var $oldWidth=$(settings.target).attr("width");var $newWidth=$(settings.target).parent().width();var $newHeight=$oldHeight/$oldWidth*$newWidth;$(settings.wrapperClass).find("iframe").css({height:$newHeight+"px",width:$newWidth+"px"});console.log("height/width values added!")})}).resize()}})(jQuery);